"use strict";
$(document).ready(function(){
    $('.toggle-menu-btn').on('click', function(){
        $('.menu-main-menu-container').slideToggle(350);
    });

    $(window).on('resize', function(){
        var docW = $(document).width();
        if(docW > 580){
            $('.menu-main-menu-container').removeAttr('style');
        }
    });
});
